import Pagination from "@/components/Pagination"; // 分页组件

import * as api from '@/api/project/eas/database/pointPreparation';
export default {
  name: "pointSelection",
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      rheight: 0,
      pheight: 0,
      sheight: 0,
      options: [],
      list: [],
      subList: null,
      total: 0,
      activeName2: "first",
      listQuery: {
        currentPage: 1,
        pageSize: 15,
        inspectionName: undefined,
        typeCode: undefined
      },
      tableKey: [0, 1]
    };
  },
  mounted: function mounted() {
    this.setTableHeight();
    this.getList();
    this.getTypeCode();
  },
  watch: {
    // 监听时间区间数组 写入到对应字段
    subList: function subList(value) {
      this.$parent.$parent.listSub = value;
    }
  },
  methods: {
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.rheight = this.$myFun.getSingleTbHeight() - 30;
      this.pheight = this.$myFun.getSingleTbHeight() + 55;
      this.sheight = this.$myFun.getSingleTbHeight() - 70;
    },
    //查询设备类型
    getTypeCode: function getTypeCode() {
      var _this = this;

      api.queryTypeCode().then(function (res) {
        _this.options = res.data;
      });
    },
    // 父级工位表格展示已选，modal已选勾选
    getListSelected: function getListSelected() {
      this.subList = this.$parent.$parent.listSub;
    },
    handleQuery: function handleQuery() {
      //查询
      this.listQuery.currentPage = 1;
      this.getList();
      this.subList = [];
    },
    selectRow: function selectRow(val) {
      this.selectRowData = val;
      this.$emit("change", this.selectRowData); // 有选中检查是否已选

      for (var i = 0; i < this.subList.length; i++) {
        if (this.subList[i].inspectionCode == val.inspectionCode) {
          // 在已选中去除该条
          this.popSelected(val);
          return false;
        }
      } // 类型检查


      if (Array.isArray(val)) {
        return false;
      } // 加入已选并勾选框


      this.subList.push(val);
      this.$refs.dd.toggleRowSelection(val, true);
    },
    // 去除已选
    popSelected: function popSelected(val) {
      var _this2 = this;

      this.subList.forEach(function (item, index) {
        if (item.inspectionCode == val.inspectionCode) {
          _this2.subList.splice(index, 1);

          _this2.$refs.dd.toggleRowSelection(val, false);
        }
      });
    },
    selectAll: function selectAll(val) {
      var _this3 = this;

      if (val.length == 0) {
        this.subList = [];
        this.$refs.dd.clearSelection();
        return false;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this3.subList.forEach(function (item) {
          if (item.inspectionCode === items.inspectionCode) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this3.subList.push(val[indexv]);
        }
      });
    },
    getList: function getList() {
      var _this4 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this4.list = res.data.list;
        _this4.total = res.data.pages.count; // 已选中的勾选状态

        _this4.subList.forEach(function (item) {
          var _loop = function _loop(i) {
            if (item.inspectionCode == _this4.list[i].inspectionCode) {
              _this4.$nextTick(function () {
                _this4.$refs.dd.toggleRowSelection(_this4.list[i], true);
              });

              return "break";
            }
          };

          for (var i = 0; i < _this4.list.length; i++) {
            var _ret = _loop(i);

            if (_ret === "break") break;
          }
        });
      });
    }
  }
};
