import request from '@/utils/request'; // 查询经验库

export function queryRecords(query) {
  return request({
    url: "/easDevice/experience/library",
    method: 'get',
    params: query
  });
} // 新增经验库

export function addRecords(data) {
  return request({
    url: "/easDevice/experience/library",
    method: 'post',
    data: data
  });
} // 修改经验库

export function patchRecords(code, data) {
  return request({
    url: "/easDevice/experience/library/".concat(code),
    method: 'patch',
    data: data
  });
} // 提交经验库

export function submitRecords(code) {
  return request({
    url: "/easDevice/experience/library/submit/".concat(code),
    method: 'patch'
  });
} // 删除经验库

export function deleteRecords(experienceCodes) {
  return request({
    url: "/easDevice/experience/library",
    method: 'delete',
    data: experienceCodes
  });
} // 查询经验库实体

export function queryExpCode(code) {
  return request({
    url: "/easDevice/experience/library/".concat(code),
    method: 'get'
  });
} // 查询故障代码

export function queryFaultCode(query) {
  return request({
    url: "/easDevice/base/fault/code",
    method: 'get',
    params: query
  });
} // 查询设备部件信息

export function queryParts(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
} // 查询经验文件

export function queryFile(query) {
  return request({
    url: "/easDevice/files",
    method: 'get',
    params: query
  });
} // 上传经验文件

export function uploadFile(code, data, query) {
  return request({
    url: "/easDevice/files/".concat(code),
    method: 'post',
    data: data,
    params: query
  });
} // 读取图片

export function queryPic(readName) {
  return request({
    url: "/easDevice/files/get?readName=".concat(readName),
    method: 'get'
  });
} // 读取图片

export function deleteFile(query) {
  return request({
    url: "/easDevice/files",
    method: 'delete',
    params: query
  });
} // 文件下载

export function download(readName) {
  return request({
    responseType: 'blob',
    url: "/easDevice/files/download?readName=".concat(readName),
    method: 'get'
  });
}
