import { selectlists as listOrg } from '@/api/system/organization';
export default {
  name: 'caigou',
  props: {
    orgType: {
      //到货单位：从组织结构中查询组织类型为“工厂”的组织信息
      type: String,
      default: ''
    },
    organizationCode: {
      type: String,
      default: ''
    },
    disabled: {
      type: Boolean,
      default: false
    },
    placeholder: {
      type: String,
      default: '请选择'
    },
    businessFunction: {
      //采购组：从组织结构中查询业务职能为“采购组”的组织信息
      type: String,
      default: ''
    }
  },
  model: {
    prop: 'organizationCode',
    event: 'change'
  },
  data: function data() {
    return {
      options: [],
      itemKey: this.organizationCode
    };
  },
  watch: {
    organizationCode: function organizationCode() {
      this.itemKey = this.organizationCode;
    }
  },
  created: function created() {
    this.getOptions();
  },
  methods: {
    getOptions: function getOptions() {
      var _this = this;

      var listQuery = {
        type: this.orgType,
        state: 1,
        businessFunction: this.businessFunction
      };
      listOrg(listQuery).then(function (res) {
        _this.options = res.data;
      });
    },
    changeSelect: function changeSelect(val) {
      this.$emit('change', val);
      var name = '';
      var org = this.options.filter(function (v) {
        return v.organizationCode == val;
      }).find(function (value) {
        return value;
      });

      if (org) {
        name = org.organizationName;
      }

      this.$emit('obj', org);
      this.$emit('selectName', name);
    },
    handleClear: function handleClear() {
      this.$emit('clear');
    }
  }
};
