import { updateRecords } from "@/api/project/srm/supplierSelect/options/option";
import formation from "./content/formation";
import tabPlanList from "./content/tabPlanList";
import tabTaskList from "./content/tabTaskList";
import dialogSetTime from "./dialog/dialogSetTime";
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory'; //物料分类选择

import dialogErrorFeedBack from "./dialog/dialogErrorFeedBack"; //物料分类选择

export default {
  name: "optionsFormation",
  components: {
    formation: formation,
    tabPlanList: tabPlanList,
    tabTaskList: tabTaskList,
    dialogSetTime: dialogSetTime,
    dialogMaterielCategory: dialogMaterielCategory,
    dialogErrorFeedBack: dialogErrorFeedBack
  },
  data: function data() {
    return {
      state: 'list',
      tabName: 'tabPlanList',
      tabConfig: [{
        label: '选择方案',
        name: 'tabPlanList',
        prop: 'tabPlanList'
      }, {
        label: '任务清单',
        name: 'tabTaskList',
        prop: 'tabTaskList'
      }],
      dialogConfig: [{
        title: '招投标时间调整'
      }, {
        title: '选择物料分类',
        isRequired: true,
        unMutiple: true
      }, {
        title: '异常反馈'
      }]
    };
  },
  created: function created() {},
  computed: {},
  mounted: function mounted() {},
  methods: {
    setFormationState: function setFormationState(fstate, data) {
      console.log(fstate, data);
      this.changeState(fstate);
      this.$refs.refForm.formationState = fstate;
      this.$refs.refForm.initForm(fstate, data);
    },
    changeState: function changeState(state) {
      this.state = state;
      this.$refs.refForm.dataListMateriel = [];
      this.$refs.refForm.dataListSupplier = [];
      this.refresh();
    },
    //分类设置
    showMC: function showMC(orgCode) {
      this.$refs.refMC.listQuery.orgCode = orgCode;
      this.$refs.refMC.handleQuery();
      this.$refs.refMC.dialogVisible = true;
    },
    handleConfirmMC: function handleConfirmMC(mdata) {
      console.log(mdata);
      this.$refs.refMC.dialogVisible = false;
      this.$refs.refTabTask[0].setMC(mdata);
    },
    showFB: function showFB() {
      this.$refs.refFB.getList();
      this.$refs.refFB.dialogVisible = true;
    },
    refresh: function refresh() {
      if (this.tabName === 'tabPlanList') {
        this.$refs.refTabPlan[0].handleQuery();
      } else if (this.tabName === 'tabTaskList') {
        this.$refs.refTabPlan[0].handleQuery();
        this.$refs.refTabTask[0].handleQuery();
      }
    },
    showST: function showST() {
      this.$refs.refSetTime.dialogVisible = true;
    },
    confirmSetTime: function confirmSetTime(data) {
      var _this = this;

      var bidTimeDTO = data;
      bidTimeDTO.planNos = [];
      this.$refs.refTabPlan[0].selectedRows.forEach(function (item) {
        bidTimeDTO.planNos.push(item.planNo);
      });
      data.planNo = this.$refs.refTabPlan[0].currentSelectedRow.planNo;
      var cData = {
        method: "UPDATE_BID_TIME",
        bidTimeDTO: bidTimeDTO
      };
      updateRecords(cData).then(function (res) {
        console.log(res, '时间设置成功');

        _this.$message({
          type: 'success',
          message: '设置成功'
        });

        _this.$refs.refTabPlan[0].handleQuery();
      });
    }
  }
};
