import RightContent from "./rightcontent";
import * as api from '@/api/project/eas/database/faultType';
export default {
  name: 'faultType',
  components: {
    RightContent: RightContent
  },
  data: function data() {
    return {
      treeData: [],
      listQuery: {
        currentPage: 1,
        pageSize: 10
      },
      defaultProps: {
        rowId: 'rowId',
        children: 'children',
        label: 'faultName'
      },
      rowIndex: '',
      //树高度样式
      contentStyleObj: {
        height: ''
      },
      selectedObj: undefined
    };
  },
  created: function created() {
    window.addEventListener('resize', this.getHeight);
    this.initTree();
  },
  mounted: function mounted() {
    var _this = this;

    //表格高度自适应
    window.onresize = function () {
      _this.setTreeHeight();
    };

    this.setTreeHeight();
    this.handleFresh();
  },
  methods: {
    buildTree: function buildTree(array, id, parent_id) {
      // 创建临时对象
      var temp = {}; // 创建需要返回的树形对象

      var tree = []; // 先遍历数组，将数组的每一项添加到temp对象中

      for (var i in array) {
        temp[array[i][id]] = array[i];
      } // 遍历temp对象，将当前子节点与父节点建立连接


      for (var _i in temp) {
        // 判断是否是根节点下的项
        if (temp[_i][parent_id] !== '0') {
          if (!temp[temp[_i][parent_id]].children) {
            temp[temp[_i][parent_id]].children = [];
          }

          temp[temp[_i][parent_id]].children.push(temp[_i]);
        } else {
          tree.push(temp[_i]);
        }
      }

      return tree;
    },
    initTree: function initTree() {
      var _this2 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this2.treeData = [{
          faultName: '故障类型',
          faultCode: '0',
          rowId: 0,
          children: _this2.buildTree(res.data.list, 'faultCode', 'preFaultCode')
        }];
      });
    },
    handleFresh: function handleFresh() {
      this.initTree();
    },
    handleNodeClick: function handleNodeClick(data) {
      this.$refs.uploadRef.getList(data);
      this.$refs.uploadRef.temp.preFaultCode = data.faultCode;
      this.selectedObj = data;
    },
    getHeight: function getHeight() {
      this.contentStyleObj.height = document.body.scrollHeight - 110 + 'px';
    },
    //树高度设置
    setTreeHeight: function setTreeHeight() {
      this.contentStyleObj.height = parseInt(this.$myFun.getTreehHeight()) - 40 + 'px';
    }
  },
  destroyed: function destroyed() {
    window.removeEventListener('resize', this.getHeight);
  }
};
