import request from '@/utils/request'; //特殊区分查询

export function queryRecords(query) {
  return request({
    url: "/basic/specialDistinction",
    method: 'get',
    params: query
  });
} //特殊区分（新增）

export function addRecord(data) {
  return request({
    url: "/basic/specialDistinction",
    method: 'post',
    data: data
  });
} //特殊区分（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/specialDistinction/".concat(rowId),
    method: 'patch',
    data: data
  });
} //特殊区分删除

export function deleteRecord(rowId) {
  return request({
    url: "/basic/specialDistinction/".concat(rowId),
    method: 'delete'
  });
}
