import request from '@/utils/request'; // 根据项目编码获取指标设备树

export function getTree(projectCode) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/indexAllocation/tree"),
    method: 'get'
  });
} // 根据项目设备编码获取设备指标信息

export function getInfo(projectCode, deviceCode, query) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/indexAllocation/").concat(deviceCode),
    method: 'get',
    params: query
  });
} // 创建自定义指标信息

export function createInfo(projectCode, deviceCode, data) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/indexAllocation/").concat(deviceCode, "/norm"),
    method: 'post',
    data: data
  });
} // 删除自定义指标信息

export function deleteInfo(projectCode, deviceCode, normCode) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/indexAllocation/").concat(deviceCode, "/norm/").concat(normCode),
    method: 'delete'
  });
} // 粒度设置

export function granularitySetting(projectCode, deviceCode, normCode, data) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/indexAllocation/").concat(deviceCode, "/granularity/").concat(normCode),
    method: 'patch',
    data: data
  });
}
