import request from '@/utils/request'; // 查询

export function queryLadder(query) {
  return request({
    url: "/ems/dtLadderPriceMaintain",
    method: 'get',
    params: query
  });
} // 查询能源分类列表

export function queryEnergyItem(query) {
  return request({
    url: "/ems/EnergyType/list",
    method: 'get',
    params: query
  });
} // 查询单个

export function queryLadderId(rowId) {
  return request({
    url: "/ems/dtLadderPriceMaintain/get/".concat(rowId),
    method: 'get'
  });
} // 新增

export function createLadder(data) {
  return request({
    url: "/ems/dtLadderPriceMaintain/create",
    method: 'post',
    data: data
  });
} // 更新

export function updateLadder(rowId, data) {
  return request({
    url: "/ems/dtLadderPriceMaintain/update/".concat(rowId),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteLadder(data) {
  return request({
    url: "/ems/dtLadderPriceMaintain/delete",
    method: 'delete',
    data: data
  });
}
