      handler: function handler(val, oldVal) {
        if (val) {
          this.getOptions();
        } else {
          this.options = [];
        }

        if (oldVal) {
          this.selectValue = undefined;
          this.$emit('change', undefined);
        }
      },
      immediate: true
    }
  },
  methods: {
    getOptions: function getOptions() {
      var _this = this;

      return request({
        url: '/base-modeling/work-centers',
        method: 'get',
        params: {
          orgCode: this.orgCode,
          workCenterType: this.workCenterType,
          workCenterClass: this.workCenterClass
        }
      }).then(function (res) {
        _this.options = res.data;

        _this.$emit('linecode', _this.options);
      });
    },
    changeSelect: function changeSelect(val) {
      var name = '';
      var dd = this.options.filter(function (v) {
        if (v.workCenterCode == val) {
          return v;
        }
      });
      var org = this.options.filter(function (v) {
        return v.workCenterCode == val;
      }).find(function (value) {
        return value;
      });

      if (org) {
        name = org.workCenterName;
      }

      this.$emit('selectName1', name);
      this.$emit('change', val);
      this.$emit('change1', dd);
    }
  }
};
