//计量仪表故障接口
import request from '@/utils/request'; // 查询

export function query(query) {
  return request({
    url: "/ems/dtModelErrInfo",
    method: 'get',
    params: query
  });
} // 查询单个

export function queryItem(rowId) {
  return request({
    url: "/ems/dtModelErrInfo/".concat(rowId),
    method: 'get'
  });
} // 新增

export function create(data) {
  return request({
    url: "/ems/dtModelErrInfo",
    method: 'post',
    data: data
  });
} // 更新

export function update(rowId, data) {
  return request({
    url: "/ems/dtModelErrInfo/".concat(rowId),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteItems(data) {
  return request({
    url: "/ems/dtModelErrInfo",
    method: 'delete',
    data: data
  });
} //查询仪表接口

export function queryeQuipmentLedger(query) {
  return request({
    url: "/ems/modelDetail/modelList",
    method: 'get',
    params: query
  });
}
