import request from "@/utils/request";
export function getDelivery(query) {
  //获取交货单
  return request({
    url: "/scc/bl/".concat(query.supplierCode),
    method: "get",
    params: query
  });
}
export function getDeliveryDetail(query) {
  //获取交货单物料详情
  return request({
    url: "/scc/bl/".concat(query.supplierCode, "/").concat(query.orderNumber),
    method: "get",
    params: query
  });
}
export function updateDelivery(datas) {
  //更新交货单
  return request({
    url: "/scc/bl/".concat(datas.supplierCode, "/").concat(datas.orderNumber),
    method: "PATCH",
    data: datas
  });
}
export function deleteDelivery(query) {
  //删除交货单
  return request({
    url: "/scc/bl/".concat(query.supplierCode, "/").concat(query.orderNumber),
    method: "delete",
    params: query
  });
}
export function createPackTag(data) {
  //创建包装标签
  return request({
    url: "/scc/bl/label",
    method: "post",
    data: data
  });
}
export function queryPackTag(query) {
  //查询包装标签
  var querystring = "";
  querystring += "materielCode=".concat(query.materielCode);
  querystring += "&orderNumber=".concat(query.orderNumber);

  for (var i = 0; i < query.orderNumbers.length; i++) {
    querystring += "&orderNumbers=".concat(query.orderNumbers[i]);
  }

  for (var _i = 0; _i < query.barcodeRuleDetails.length; _i++) {
    querystring += "&barcodeRuleDetails=".concat(query.barcodeRuleDetails[_i]);
  }

  return request({
    url: "/scc/bl/label?".concat(querystring),
    method: "get"
  });
}
export function updatePackTag(data) {
  //更新包装标签;标签打印设置
  return request({
    url: "/scc/bl/label",
    method: "patch",
    data: data
  });
}
