import * as api from '@/api/project/ems/onlineMonitoring/online.js'; // import Bar from '@/views/project/EMS/onlineMonitoring/processMonitoring/temp/bar.vue'

export default {
  name: 'workShopWarning',
  //工序监控与预警
  // components: { Bar },
  data: function data() {
    return {
      theight: 0,
      //表格高度
      list: [],
      isMore: false,
      loading: true,
      listQuery: {
        warningName: 'workShopWarning'
      },
      remark: [{
        class: 'fault',
        info: '水'
      }, {
        class: 'standby',
        info: '电'
      }, {
        class: 'run',
        info: '气'
      }],
      setTimeoutValue: null
    };
  },
  computed: {
    holder: function holder() {
      return this.list.length % 6 == 0 ? 0 : 6 - this.list.length % 6;
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  destroyed: function destroyed() {
    this.setTableHeight();
    this.clear();
  },
  methods: {
    showMore: function showMore() {
      this.isMore = !this.isMore;
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      api.query(this.listQuery).then(function (response) {
        _this2.list = response.data;
        _this2.loading = false;
      });
    },
    setTimeout: function setTimeout() {
      var _this3 = this;

      this.setTimeoutValue = setInterval(function () {
        _this3.getList();
      }, 60000 * 5);
    },
    clear: function clear() {
      if (this.setTimeoutValue) {
        clearInterval(this.setTimeoutValue);
      }
    }
  }
};
