import request from '@/utils/request'; // 查询设备类型信息列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/base/fault",
    method: 'get',
    params: query
  });
}
export function queryDetails(faultTypeCode) {
  return request({
    url: "/easDevice/base/fault/".concat(faultTypeCode),
    method: 'get'
  });
} //新增

export function addRecord(data) {
  return request({
    url: "/easDevice/base/fault",
    method: 'post',
    data: data
  });
} //编辑

export function updateRecord(faultTypeCode, data) {
  return request({
    url: "/easDevice/base/fault/".concat(faultTypeCode),
    method: 'patch',
    data: data
  });
} //删除

export function deleteRecord(faultTypeCode) {
  return request({
    url: "/easDevice/base/fault/".concat(faultTypeCode),
    method: 'delete'
  });
}
