import "core-js/modules/web.dom.iterable";
import request from '@/utils/request'; //查询保养工单

export function queryRecords(query, states) {
  var data = '';
  states.forEach(function (i, index) {
    if (index == 0) {
      data += "states=".concat(i);
    } else {
      data += "&states=".concat(i);
    }
  });
  return request({
    url: "/easDevice/upkeep/work/order?".concat(data),
    method: 'get',
    params: query
  });
} // 查询从表

