import request from '@/utils/request'; //供应商属性字段查询

export function queryRecordAttribute(query) {
  return request({
    url: "/SRMsupplier/supplier/column",
    method: 'get',
    params: query
  });
} //供应商变更配置主信息查询

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/supplier/modify/config",
    method: 'get',
    params: query
  });
} //供应商变更配置主信息（新增）

export function addRecord(data) {
  return request({
    url: "/SRMsupplier/supplier/modify/config",
    method: 'post',
    data: data
  });
} //供应商变更配置主信息（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(rowId),
    method: 'patch',
    data: data
  });
} //供应商变更配置主信息删除

export function deleteRecord(rowId) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(rowId),
    method: 'delete'
  });
} //供应商变更配置明细查询

export function queryRecordItem(configId) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(configId, "/item"),
    method: 'get'
  });
} //供应商变更配置明细（新增）

export function addRecordItem(configId, data) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(configId, "/item"),
    method: 'post',
    data: data
  });
} //供应商变更配置明细删除

export function deleteRecordItem(configId, rowId) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(configId, "/item/").concat(rowId),
    method: 'delete'
  });
} //供应商变更配置（生效与注销）

export function effect(rowId, data) {
  return request({
    url: "/SRMsupplier/supplier/modify/config/".concat(rowId, "/state"),
    method: 'patch',
    data: data
  });
}
