import request from '@/utils/request'; // 查询网关产品基本信息列表

export function queryRecords(query) {
  return request({
    url: "/iotIot/iot/gateway",
    method: 'get',
    params: query
  });
} // 查询单个网关产品基本信息

export function queryRecord(gatewayCode) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode),
    method: 'get'
  });
} // 创建网关产品基本信息

export function addRecord(data) {
  return request({
    url: "/iotIot/iot/gateway",
    method: 'post',
    data: data
  });
} // 删除网关产品基本信息

export function deleteRecord(gatewayCode) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode),
    method: 'delete'
  });
} // 更新网关产品基本信息

export function updateRecord(gatewayCode, data) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode),
    method: 'patch',
    data: data
  });
} // 查询网关产品Topic类列表

export function queryTopicS(gatewayCode, query) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode, "/topic"),
    method: 'get',
    params: query
  });
} // 查询单个网关产品Topic类

export function queryTopic(gatewayCode, rowId) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode, "/topic/").concat(rowId),
    method: 'get'
  });
} // 创建网关产品Topic类

export function addTopic(gatewayCode, data) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode, "/topic"),
    method: 'post',
    data: data
  });
} // 更新网关产品Topic类

export function updateTopic(gatewayCode, rowId, data) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode, "/topic/").concat(rowId),
    method: 'patch',
    data: data
  });
}
export function deleteTopic(gatewayCode, rowId) {
  return request({
    url: "/iotIot/iot/gateway/".concat(gatewayCode, "/topic/").concat(rowId),
    method: 'delete'
  });
}
