import request from '@/utils/request'; // 查询物料替代组列表

export function queryRecord(query) {
  return request({
    url: "/basic/materiel-replace",
    method: 'get',
    params: query
  });
} // 新增物料替代组

export function addRecord(data) {
  return request({
    url: "/basic/materiel-replace",
    method: 'post',
    data: data
  });
} // 新增物料替代组明细

export function addRecordItem(data) {
  return request({
    url: "/basic/materiel-replace/detail",
    method: 'post',
    data: data
  });
} // 查询单个物料替代组

export function queryOneRecord(groupCode) {
  return request({
    url: "/basic/materiel-replace/detail/".concat(groupCode),
    method: 'get'
  });
} // 删除物料替代组

export function deleteRecord(rowId) {
  return request({
    url: "/basic/materiel-replace/".concat(rowId),
    method: 'delete'
  });
} // 删除单个物料替代组明细

export function deleteOneRecord(rowId) {
  return request({
    url: "/basic/materiel-replace/detail/".concat(rowId),
    method: 'delete'
  });
} // 修改物料替代组

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/materiel-replace/".concat(rowId),
    method: 'patch',
    data: data
  });
}
