import * as api from '@/api/project/eas/database/groups';
import Pagination from '@/components/Pagination';
export default {
  name: 'teamEmployeeSelect',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      list: [],
      tableKey: [0],
      total: 0,
      theight: 0,
      //表格高度
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10
      },
      temp: {
        classCode: undefined,
        description: undefined,
        address: undefined,
        deviceCode: undefined,
        deviceName: undefined,
        faultCode: undefined,
        faultGrade: undefined,
        faultTypeCode: undefined,
        happenTime: undefined,
        ifMachineHalt: undefined,
        machineHaltTime: undefined,
        mobile: undefined,
        operator: undefined,
        operatorName: undefined,
        partCode: undefined
      }
    };
  },
  props: {
    teamCode: {
      type: String,
      default: undefined
    }
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //查询主表
    getList: function getList() {
      var _this2 = this;

      api.queryTeamEmployee(this.teamCode, this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 185;
    },
    //查询
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val[0];

      if (val.length > 1) {
        this.$refs.mainList.clearSelection();
        this.$refs.mainList.toggleRowSelection(val.pop());
      }

      this.$emit('change', this.selectlistRow);
    },
    //主表行点击
    showSubTable: function showSubTable(row) {
      this.$refs.mainList.clearSelection();
      this.$refs.mainList.toggleRowSelection(row);
    }
  }
};
