import * as api from '@/api/project/eas/database/faultType';
import Pagination from '@/components/Pagination';
import selectTree from '@/components/Select/selectTree'; //下拉组件

import { queryRecords } from '@/api/project/eas/database/faultType'; // import SelectTree from '@/components/SelectTree/index.vue'

export default {
  name: 'faultTypeRightContent',
  components: {
    Pagination: Pagination,
    selectTree: selectTree
  },
  props: ['rowIndex'],
  data: function data() {
    return {
      list: null,
      total: 0,
      optionData: [],
      isClearable: true,
      // 可清空（可选）
      isAccordion: true,
      // 可收起（可选）
      valueId: undefined,
      // 初始ID（可选）
      props: {
        // 配置项（必选）
        rowId: 'rowId',
        // 父级唯一标识
        children: 'children',
        label: 'faultName'
      },
      DT_ORG_TYPE: 'dt_org_type_factory',
      theight: 0,
      //表格高度
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        faultCode: undefined,
        faultName: undefined,
        preFaultCode: undefined
      },
      listQueryNode: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        preFaultCode: undefined,
        faultCode: undefined,
        faultName: undefined
      },
      faultCode: undefined,
      faultName: undefined,
      temp: {
        faultCode: undefined,
        faultName: undefined,
        preFaultCode: undefined,
        remark: undefined
      },
      preFaultCode: undefined,
      dialogFormVisible: false,
      dialogStatus: '',
      tableKey: 0
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getListOne();
    this.getOptionData();
  },
  methods: {
    buildTree: function buildTree(array, id, parent_id) {
      var temp = {};
      var tree = [];

      for (var i in array) {
        temp[array[i][id]] = array[i];
      }

      for (var _i in temp) {
        if (temp[_i][parent_id] != 0) {
          if (!temp[temp[_i][parent_id]].children) {
            temp[temp[_i][parent_id]].children = [];
          }

          temp[temp[_i][parent_id]].children.push(temp[_i]);
        } else {
          tree.push(temp[_i]);
        }
      }

      return tree;
    },
    getValue: function getValue(value) {
      this.temp.preFaultCode = value.faultCode;
    },
    getList: function getList(node) {
      var _this2 = this;

      if (node) {
        this.listQueryNode.preFaultCode = node.faultCode;
        api.queryRecords(this.listQueryNode).then(function (res) {
          _this2.list = res.data.list;
          _this2.total = res.data.pages.count;
        });
      }
    },
    getListOne: function getListOne() {
      var _this3 = this;

      api.queryRecords(this.listQueryNode).then(function (res) {
        _this3.list = res.data.list;
        _this3.total = res.data.pages.count;
      });
    },
    getOptionData: function getOptionData() {
      var _this4 = this;

      queryRecords().then(function (res) {
        _this4.optionData = [{
          faultName: '故障类型',
          faultCode: '0',
          rowId: 0,
          children: _this4.buildTree(res.data, 'faultCode', 'preFaultCode')
        }];
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleChange: function handleChange() {
      this.$emit('fresh');
    },
    //查询
    handleQuery: function handleQuery() {
      this.listQueryNode.currentPage = 1;
      this.getListOne();
    },
    //新增弹窗
    handleCreate: function handleCreate() {
      var _this5 = this;

      if (this.listQueryNode.preFaultCode) {
        this.resetTemp();
        this.temp.preFaultCode = '';
        this.dialogStatus = 'create';
        this.dialogFormVisible = true;
        this.temp.preFaultCode = this.listQueryNode.preFaultCode;
        this.$nextTick(function () {
          _this5.$refs['temp'].clearValidate();

          _this5.$refs.errSelect.valueTitle = _this5.$parent.$parent.$parent.selectedObj.faultName;
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择左边上级菜单',
          type: 'warning'
        });
      }
    },
    //新增方法
    create: function create() {
      var _this6 = this;

      api.addRecord(this.temp).then(function () {
        _this6.getList(_this6.$parent.$parent.$parent.selectedObj);

        _this6.handleChange();

        _this6.getOptionData();

        _this6.dialogFormVisible = false;

        _this6.$notify({
          title: '成功',
          message: '新增成功',
          type: 'success',
          duration: 2000
        });
      });
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    //编辑弹窗
    handleUpdate: function handleUpdate() {
      var _this7 = this;

      if (!this.listQueryNode.preFaultCode) {
        this.$message.warning('请选择对应上级故障类型编码！');
        return;
      }

      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.temp = this.selectlistRow[0]; // copy obj

        this.dialogStatus = 'update';
        this.dialogFormVisible = true;
        this.temp.preFaultCode = this.listQueryNode.preFaultCode;
        this.$nextTick(function () {
          _this7.$refs['temp'].clearValidate();

          _this7.$refs.errSelect.valueTitle = _this7.$parent.$parent.$parent.selectedObj.faultName;
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    //编辑
    update: function update() {
      var _this8 = this;

      var faultTypeCode = this.selectlistRow[0].faultCode;
      api.updateRecord(faultTypeCode, this.temp).then(function () {
        _this8.getList(_this8.$parent.$parent.$parent.selectedObj);

        _this8.getOptionData();

        _this8.handleChange();

        _this8.dialogFormVisible = false;

        _this8.$message({
          title: '成功',
          message: '更新成功',
          type: 'success',
          duration: 2000
        });
      });
    },
    //删除列表
    handleDelete: function handleDelete() {
      var _this9 = this;

      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          var faultTypeCode = _this9.selectlistRow[0].faultCode;
          api.deleteRecord(faultTypeCode).then(function () {
            _this9.getList(_this9.$parent.$parent.$parent.selectedObj);

            _this9.getListOne();

            _this9.getOptionData();

            _this9.handleChange();

            _this9.$message({
              title: '成功',
              message: '删除成功',
              type: 'success',
              duration: 2000
            });
          });
        }).catch(function () {
          _this9.$message({
            type: 'info',
            message: '已取消删除'
          });
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    resetTemp: function resetTemp() {
      this.temp = {
        faultCode: undefined,
        faultName: undefined,
        preFaultCode: undefined,
        remark: undefined
      };
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    }
  }
};
