import request from '@/utils/request'; // 查询盘点计划信息

export function queryRecord(query) {
  return request({
    url: "/easDevice/check/checkPlan",
    method: 'get',
    params: query
  });
}
export function organizations(query) {
  //查询组织列表
  return request({
    url: '/keyguard/organizations',
    method: 'get',
    params: query
  });
} // 查询设备类型信息列表

export function queryEqmt(query) {
  return request({
    url: '/easDevice/base/deviceType',
    method: 'get',
    params: query
  });
} // 查询设备台账

export function queryMainlist(query) {
  return request({
    url: "/easDevice/file/device",
    method: 'get',
    params: query
  });
} // 新增盘点计划

export function createRecord(data) {
  return request({
    url: "/easDevice/check/checkPlan",
    method: 'post',
    data: data
  });
} // 删除盘点计划

export function deleteRecord(data) {
  return request({
    url: "/easDevice/check/checkPlan",
    method: 'delete',
    data: data
  });
} // 查询盘点计划项目

