        legend: {
          left: 'center',
          top: '6%',
          data: this.legend
        },
        series: [{
          type: 'pie',
          radius: '60%',
          center: ['50%', '55%'],
          selectedMode: 'single',
          itemStyle: {
            normal: {
              color: function color(params) {
                //自定义颜色
                var color = ['#fd6361', '#02b7ae', '#606b6d', '#05b7ab', '#fd6361', '#ff9669', '#37454e'];
                return color[params.dataIndex];
              }
            }
          },
          data: this.chartData.data
        }]
      };
      this.$nextTick(function () {
        _this.myChart = _this.$echarts.init(_this.$refs[_this.chartData.id]);

        _this.myChart.setOption(opt, true);
