import { parseTime } from '@/utils';
import ImportFile from "@/components/ImportFile";
export default {
  name: "sourceList",
  components: {
    Pagination: Pagination,
    orgSelect: orgSelect,
    gysxz: gysxz,
    wlxzbyzsj: wlxzbyzsj,
    ImportFile: ImportFile
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      parentsRows: null,
      parentsRows1: null,
      theight: 0,
      //表格高度
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        materielCode: undefined,
        supplierCode: undefined,
        orgCode: undefined
      },
      temp: {
        materielCode: undefined,
        supplierCode: undefined,
        orgCode: undefined,
        materielName: undefined,
        state: 1,
        supplierName: undefined,
        stockContain: undefined
      },
      importDialogVisible: false,
      //导入弹框
      modalNo: 'EXTP-BAS-0007',
      //导入模板编号
      orgCode: undefined,
      selectCode: [],
      dialogFormVisible: false,
      materielDialogFormVisible: false,
      gysxzDialogFormVisible: false,
      dialogStatus: "",
      tableKey: 0,
      DT_ORG_FUNCTION: DT_ORG_FUNCTION,
      rules: {
        customerCode: [{
          required: true,
          message: "客户编码不能为空",
          trigger: "blur"
        }, {
          pattern: /^[A-Z-_0-9]{1,50}$/,
          message: "由大写字母，数字，—和_组成，长度不能超过50",
          trigger: "blur"
        }],
        customerName: [{
          required: true,
          message: "客户名称不能为空",
          trigger: "blur"
        }, {
          max: 100,
          message: "长度不能超过100",
          trigger: "blur"
        }],
        contact: [{
          max: 50,
          message: "长度不能超过50",
          trigger: "blur"
        }],
        contactNumber: [{
          validator: validatorContactNumber,
          trigger: "blur"
        }],
        email: [{
          validator: validatorEmail,
          trigger: "blur"
        }],
        address: [{
          max: 512,
          message: "长度不能超过512",
          trigger: "blur"
        }],
        remark: [{
          max: 512,
          message: "描述长度为512内",
          trigger: "blur"
        }]
      }
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this2 = this;

      selectlists().then(function (response) {
        api.queryRecords(_this2.listQuery).then(function (res) {
          res.data.list.forEach(function (item, index) {
            response.data.forEach(function (option) {
              if (option.organizationCode == item['orgCode']) {
                res.data.list[index]['orgCode' + 'CN'] = option.organizationName;
              }
            });
          });
          _this2.list = res.data.list;
          _this2.total = res.data.pages.count;
        });
      }), api.queryRecords(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    },
    handleCreate: function handleCreate() {
      this.resetTemp();
      this.dialogStatus = "create";
      this.dialogFormVisible = true;
    },
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    handleUpdate: function handleUpdate() {
      var _this3 = this;

      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.readonly = true; //组织编码不可以编写

        this.temp = this.selectlistRow[0]; // copy obj

        this.dialogStatus = "update";
        this.dialogFormVisible = true;
        this.$nextTick(function () {
          _this3.$refs["temp"].clearValidate();
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    update: function update() {
      var _this4 = this;

      this.$refs["temp"].validate(function (valid) {
        if (valid) {
          api.updateRecord(_this4.temp.rowId, _this4.temp).then(function () {
            _this4.getList();

            _this4.dialogFormVisible = false;

            _this4.$message({
              title: "成功",
              message: "更新成功",
              type: "success",
              duration: 2000
            });
          });
        }
      });
    },
    mydata: function mydata() {},
    handleDelete: function handleDelete() {
      var _this5 = this;

      //删除列表
      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.$confirm("此操作将删除所选中数据, 是否继续?", "提示", {
          confirmButtonText: "确定",
          cancelButtonText: "取消",
          type: "warning"
        }).then(function () {
          var ids = [];

          _this5.selectlistRow.forEach(function (val, index) {
            ids[index] = val.rowId;
          });

          api.deleteRecord(ids).then(function () {
            _this5.getList(), _this5.$message({
              title: "成功",
              message: "删除成功",
              type: "success",
              duration: 2000
            });
          });
        }).catch(function () {
          _this5.$message({
            type: "info",
            message: "已取消删除"
          });
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    create: function create() {
      var _this6 = this;

      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.addRecord(_this6.temp).then(function () {
            _this6.getList();

            _this6.dialogFormVisible = false;

            _this6.$notify({
              title: "成功",
              message: "新增成功",
              type: "success",
              duration: 2000
            });
          });
        } else {
          return false;
        }
      });
    },
    resetTemp: function resetTemp() {
      this.temp = {
        materielCode: undefined,
        supplierCode: undefined,
        orgCode: undefined,
        materielName: undefined,
        stockContain: undefined,
        state: 1,
        supplierName: undefined
      };
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    handleClickgysxz: function handleClickgysxz() {
      this.$refs.gysxz.dialogVisible = true;
    },
    // handleDblclickgysxz (row) {//父子页面通信
    //     this.parentsRows = row;
    // },
    handleGysClick: function handleGysClick(row) {
      //供应商选中弹窗确认按钮
      this.$set(this.temp, 'supplierCode', row[0].supplierCode);
      this.$set(this.temp, 'supplierName', row[0].supplierName);
    },
    handleClickMateriel: function handleClickMateriel() {
      var _this7 = this;

      this.materielDialogFormVisible = true;
      this.$nextTick(function () {
        _this7.$refs.wlxz.getList();
      });
    },
    // handleDblclickMateriel (row) {
    //     this.parentsRows1 = row;
    //     this.parentsRows1.snp = this.parentsRows1.stockContain;
    //     this.temp.materialQuality = this.parentsRows1.materialQuality;
    //     this.temp.materielCode = this.parentsRows1.materielCode;
    //     this.temp.materielName = this.parentsRows1.materielName;
    //     this.temp.materielClassify = this.parentsRows1.materielClassify;
    //     this.temp.materielGroup = this.parentsRows1.materielGroup;
    //     this.temp.measuringUnit = this.parentsRows1.measuringUnit;
    //     this.temp.measuringUnitName = this.parentsRows1.measuringUnitName;
    //     this.temp.materielType = this.parentsRows1.materielType;
    //     this.temp.specification = this.parentsRows1.specification;
    //     this.materielDialogFormVisible = false;
    // },
    wlxzConfirm: function wlxzConfirm() {
      if (this.$refs.wlxz.listSelected.length > 0) {
        this.$set(this.temp, 'materielName', this.$refs.wlxz.listSelected[0].materielName);
        this.$set(this.temp, 'materielCode', this.$refs.wlxz.listSelected[0].materielCode);
        this.$set(this.temp, 'materielClassify', this.$refs.wlxz.listSelected[0].materielClassify);
        this.$set(this.temp, 'materielType', this.$refs.wlxz.listSelected[0].materielType);
        this.materielDialogFormVisible = false;
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    selectName: function selectName(val) {
      this.temp.orgCode = val;
    },
    handleDownload: function handleDownload() {
      var _this8 = this;

      this.downloadLoading = true;
      import('@/vendor/Export2Excel').then(function (excel) {
        var tHeader = ['组织编码', '组织名称', '物料编码', '物料名称', "供应商编码", "供应商名称", '状态'];
        var filterVal = ['orgCode', 'orgName', 'materielCode', 'materielName', "supplierCode", "supplierName", 'state'];

        var data = _this8.formatJson(filterVal, _this8.list);

        excel.export_json_to_excel({
          header: tHeader,
          data: data,
          filename: 'table-list'
        });
        _this8.downloadLoading = false;
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          if (j === 'timestamp') {
            return parseTime(v[j]);
          } else {
            return v[j];
          }
        });
      });
    },
    handleImport: function handleImport() {
      var _this9 = this;

      this.importDialogVisible = true;
      this.$nextTick(function () {
        _this9.$refs.importDialog.getList();
      });
    }
  }
};
