import request from '@/utils/request'; // 查询设备列表

export function getEquipmentAll(query) {
  return request({
    url: "/mes/equipmentLedger",
    method: 'get',
    params: query
  });
}
export function querySuppierRecords() {
  //查询设备厂商
  return request({
    url: '/mes/suppliers',
    method: 'get',
    params: {
      currentPage: 1,
      pageSize: 50
    }
  });
}
export function equipmentType() {
  //查询设备类别
  return request({
    url: "/mes/equipmentType",
    method: 'get'
  });
}
export function queryRecords(query) {
  return request({
    url: '/mes/inspection/standards',
    method: 'get',
    params: query
  });
}
