import request from '@/utils/request'; // 查询

export function queryYieldEnergy(query) {
  return request({
    url: "/ems/setSpecificYieldEnergy",
    method: 'get',
    params: query
  });
} // 查询组织结构

export function queryOrg() {
  var query = {
    type: 'dt_org_type_factory'
  };
  return request({
    url: "/keyguard/organizations",
    method: 'get',
    params: query
  });
} // 新增

export function createYieldEnergy(data) {
  return request({
    url: "/ems/setSpecificYieldEnergy",
    method: 'post',
    data: data
  });
} // 更新

export function updateYieldEnergy(code, data) {
  return request({
    url: "/ems/setSpecificYieldEnergy/".concat(code),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteYieldEnergy(data) {
  return request({
    url: "/ems/setSpecificYieldEnergy",
    method: 'delete',
    data: data
  });
}
