import { queryRecords as getWorkCenter } from '@/api/project/eas/database/work-center';
export default {
  name: 'useDeptSelect',
  props: {
    workCenterClass: {
      //工位类别：从组织结构中查询组织类型为“生产线”的组织信息
      type: String,
      default: ''
    },
    workCenterCode: {
      type: String,
      default: ''
    },
    disabled: {
      type: Boolean,
      default: false
    },
    placeholder: {
      type: String,
      default: '请选择'
    }
  },
  model: {
    prop: 'workCenterCode',
    event: 'change'
  },
  data: function data() {
    return {
      options: [],
      itemKey: this.workCenterCode
    };
  },
  watch: {
    workCenterCode: function workCenterCode() {
      this.itemKey = this.workCenterCode;
    }
  },
  created: function created() {
    this.getOptions();
  },
  methods: {
    getOptions: function getOptions() {
      var _this = this;

      getWorkCenter({
        workCenterClass: this.workCenterClass
      }).then(function (res) {
        _this.options = res.data;
      });
    },
    changeSelect: function changeSelect(val) {
      this.$emit('change', val);
      var name = '';
      var org = this.options.filter(function (v) {
        return v.workCenterCode == val;
      }).find(function (value) {
        return value;
      });

      if (org) {
        name = org.workCenterName;
      }

      this.$emit('selectName', name);
    }
  }
};
