import request from '@/utils/request'; //需求汇总信息查询

export function queryRecords(query) {
  return request({
    url: "/spm/requisitions/plus",
    method: 'get',
    params: query
  });
} //需求汇总（汇总）

export function addTotal(data) {
  return request({
    url: "/spm/requisitions/sum",
    method: 'post',
    data: data
  });
} //需求分工信息查询

export function queryRecordsDivision(query) {
  return request({
    url: "/spm/requisitions/sum",
    method: 'get',
    params: query
  });
} //需求分工（分工）

export function updateRecord(data) {
  return request({
    url: "/spm/requisitions/sum",
    method: 'patch',
    data: data
  });
} //需求分工（发布）

export function publish(data) {
  return request({
    url: "/spm/requisitions/sum/issue",
    method: 'patch',
    data: data
  });
}
