// RFQ应答
import request from '@/utils/request'; //分页查询RFQ应答列表

export function queryRFQRecords(query) {
  return request({
    url: '/business/rfq-reply',
    method: 'get',
    params: query
  });
} //参考报价查询

export function queryPriceRecords(query) {
  return request({
    url: "/business/quotation/quotation-compare",
    method: 'get',
    params: query
  });
} //提交技术标、提交报价标、技术应答

export function handleRecords(data) {
  return request({
    url: "/business/rfq-reply/handle",
    method: 'patch',
    data: data
  });
} //查询单个查询报价标

export function queryQuoteRow(params) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal`,
    url: "/business/quotation",
    method: 'get',
    params: params
  });
} //创建报价标

export function createQuote(data) {
  return request({
    // url: '/business/rfq-reply/quoted-proposal',
    url: '/business/quotation',
    method: 'post',
    data: data
  });
} //更新报价标

export function updateQuote(data) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal`,
    url: "/business/quotation",
    method: 'patch',
    data: data
  });
} //创建报价标明细

export function createQuoteItem(data) {
  return request({
    // url: '/business/rfq-reply/quoted-proposal/items',
    url: '/business/quotation/items',
    method: 'post',
    data: data
  });
} //查询单个查询报价标明细

export function queryQuoteItem(params) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal/items/${params.rowId}`,
    url: "/business/quotation/items/".concat(params.rowId),
    method: 'get',
    params: params
  });
} //更新报价标明细

export function updateQuoteItem(rowId, data) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal/items/${rowId}`,
    url: "/business/quotation/items/".concat(rowId),
    method: 'patch',
    data: data
  });
} //查询报价标明细

export function queryQuoteDetails(params) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal/${params.parentRowId}/items`,
    url: "/business/quotation/".concat(params.parentRowId, "/items"),
    method: 'get',
    params: params
  });
} //删除报价标明细

export function deleteQuoteItem(params) {
  return request({
    // url: `/business/rfq-reply/quoted-proposal/${params.parentRowId}/items/${
    //     params.rowId
    // }`,
    url: "/business/quotation/".concat(params.parentRowId, "/items/").concat(params.rowId),
    method: 'delete',
    params: params
  });
} //查询参考报价列表

export function queryRefQuote(parmas) {
  return request({
    // url: `/rfq-reply/reference-quotation`,
    url: "/business/quotation/reference-quotation",
    method: 'get',
    parmas: parmas
  });
} //查询RFQ应答明细

export function queryRFQItems(rfqNo, parmas) {
  return request({
    url: "/business/rfq-reply/".concat(rfqNo, "/items"),
    method: 'get',
    parmas: parmas
  });
} //查询采购方案列表

export function queryRecords(planNo) {
  return request({
    url: "/business/purchase-plans/".concat(planNo),
    method: 'get'
  });
}
