import * as api from '@/api/data-base/warehouse-distribute';
import Pagination from '@/components/Pagination';
import { DICT_CODE, TWO_STATE_OPTIONS, DT_ORG_TYPE } from '@/utils/constant';
export default {
  name: 'wlxz',
  components: {
    Pagination: Pagination
  },
  props: ['warehouseCode', 'orgCode', 'materielselect', 'unMutiple', 'toOrgCode'],
  data: function data() {
    return {
      list: null,
      listSelected: [],
      total: 0,
      parentsRows: null,
      areaList: null,
      locatorList: [],
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        materielCode: undefined,
        supplierCode: undefined,
        orgCode: undefined,
        toOrgCode: undefined,
        warehouseCode: this.warehouseCode
      },
      selectCode: [],
      tableKey: 0,
      activeName2: 'first',
      DICT_CODE: DICT_CODE,
      TWO_STATE_OPTIONS: TWO_STATE_OPTIONS,
      DT_ORG_TYPE: DT_ORG_TYPE
    };
  },
  methods: {
    getList: function getList() {
      var _this = this;

      api.queryRecords(this.listQuery).then(function (res) {
        console.log(res, '666');
        _this.list = res.data.list.map(function (v) {
          v.wareAreaCode = v.areaCode;
          v.wareLocationCode = v.locationCode;
          return v;
        });
        _this.total = res.data.pages.count;
      });
    },
    handleDelete: function handleDelete() {
      var _this2 = this;

      //删除方法
      if (this.selectlistRow) {
        this.$confirm(this.$t('message.isDeleteContinue'), this.$t('message.prompt'), {
          confirmButtonText: this.$t('message.yes'),
          cancelButtonText: this.$t('message.no'),
          type: 'warning'
        }).then(function () {
          api.deleteRecord(_this2.selectlistRow[0].rowId).then(function () {
            _this2.getList();

            _this2.$notify({
              title: '成功',
              message: '删除成功',
              type: 'success',
              duration: 2000
            });
          });
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    }
  },
  watch: {
    warehouseCode: {
      handler: function handler(newValue) {
        this.listQuery.warehouseCode = newValue;
        this.getList();
      },
      deep: true
    }
  }
};
