import request from '@/utils/request'; // 日常评价查询

export function queryRecord(query) {
  return request({
    url: "/SRMsupplier/evaluationSheet/items",
    method: 'get',
    params: query
  });
} // 评分，提交日常评价

export function submitRecord(evaluationNo, rowId, data) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(evaluationNo, "/items/").concat(rowId),
    method: 'patch',
    data: data
  });
} // 查询单个评价项目

export function queryOneItem(rowId) {
  return request({
    url: "/SRMsupplier/evaluationSheet/".concat(rowId, "/details"),
    method: 'get'
  });
}
