// 库内加工凭证接口
import request from '@/utils/request'; // 库内计划创建

export function createRecords(data) {
  return request({
    url: '/iwms/stores/plans/processings',
    method: 'post',
    data: data
  });
} //库内计划删除  /stores/plans/processings

export function deleteRecords(ProcessingPlanList) {
  return request({
    url: "/iwms/stores/plans/processings",
    method: 'delete',
    data: ProcessingPlanList
  });
} //库内计划批量发布,关闭(传 批量勾选的版本号 数组)

export function publishRecords(data) {
  return request({
    url: "/iwms/stores/plans/processings",
    method: 'patch',
    data: data
  });
} //库内计划主信息查询

export function queryRecords(query) {
  return request({
    url: "/iwms/stores/plans/processings/".concat(query.orgCode, "/").concat(query.warehouseCode, "/").concat(query.dicType),
    method: 'get',
    params: query
  });
} //库内计划更新  /stores/plans/processings/{planNo}

export function updateRecords(planNo, data) {
  return request({
    url: "/iwms/stores/plans/processings/".concat(planNo),
    method: 'patch',
    data: data
  });
} //库内计划明细信息查询

export function queryRecordsDetail(query) {
  return request({
    url: "/iwms/stores/plans/processings/detail",
    method: 'get',
    params: query
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
} //获取扣料清单明细

export function getKlDetail(versions) {
  return request({
    url: "/iwms/stores/boms/".concat(versions, "/detail"),
    method: 'get'
  });
} // 创建库内加工凭证

export function createProof(data) {
  return request({
    url: '/iwms/stores/processings',
    method: 'post',
    data: data
  });
} //

export function queryStock(code) {
  return request({
    url: '/iwms/warehouses/rules',
    method: 'get',
    params: {
      currentPage: 0,
      pageSize: 15,
      wareRuleCode: code,
      wareRuleName: '',
      statsClassify: ''
    }
  });
}
