import request from '@/utils/request'; // 查询备件请购汇总列表

export function queryRecords(query) {
  return request({
    url: '/spm/requisitions/sum',
    method: 'get',
    params: query
  });
} // 修改备件请购汇总记录-批量修改

export function patchRecords(data) {
  return request({
    url: '/spm/requisitions/sum',
    method: 'patch',
    data: data
  });
} // 生成订单

export function generateOrder(ids) {
  return request({
    url: '/spm/requisitions/sum/createOrder',
    method: 'post',
    data: ids
  });
}
