import request from '@/utils/request'; //上传

export function upload(functionCode, data, query) {
  return request({
    url: "/basic/files/".concat(functionCode),
    method: 'post',
    data: data,
    params: query
  });
} //附件查询

export function queryRecords(query) {
  return request({
    url: "/basic/files",
    method: 'get',
    params: query
  });
} //附件下载

export function downLoad(query) {
  return request({
    url: "/basic/files/download",
    method: 'get',
    params: query
  });
} //附件删除

export function deleteRecord(query) {
  return request({
    url: "/basic/files",
    method: 'delete',
    params: query
  });
}
