import request from '@/utils/request';
export function getData_a(query) {
  //查询入库质检单列表
  return request({
    url: "/iwms/stores/iqc",
    method: 'get',
    params: query
  });
}
export function getData_a_1(query) {
  //查询入库质检单明细列表
  return request({
    url: "/iwms/stores/iqc/".concat(query.iqcOrderNumber, "/items"),
    method: 'get',
    params: query
  });
}
export function update_a(query) {
  //更新入库质检单记录
  return request({
    url: "/iwms/stores/iqc/".concat(query.iqcOrderNumber),
    method: 'PATCH',
    data: query
  });
}
export function getData_a_2(query) {
  //查询入库质检明细记录
  return request({
    url: "/iwms/stores/iqc/".concat(query.iqcOrderNumber, "/items/").concat(query.iqcRowNo),
    method: 'GET'
  });
}
export function update_a_1(query, temp) {
  //更新入库质检单明细记录
  return request({
    url: "/iwms/stores/iqc/".concat(query.iqcOrderNumber, "/items/").concat(query.iqcRowNo),
    method: 'PATCH',
    data: temp
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
export function recommend(query) {
  //库区库位推荐
  return request({
    url: "/iwms/ruleExecution/locationPropose",
    method: 'POST',
    data: query
  });
}
