export default {
  name: "bar",
  props: ['id', 'data'],
  data: function data() {
    return {
      mycharts: undefined
    };
  },
  watch: {
    data: function data() {
      this.getPie();
    }
  },
  mounted: function mounted() {
    var _this = this;

    window.onresize = function () {
      _this.mycharts.resize();

      _this.getPie();
    };

    this.getPie();
  },
  methods: {
    getPie: function getPie() {
      if (!this.data) return false;
      var legend = this.data.legend ? this.data.legend : [];
      var xAxisData = this.data.xAxisData ? this.data.xAxisData : [];
      var data = this.data.data.map(function (res) {
        return {
          name: res.name,
          type: 'bar',
          stack: '总量',
          label: {
            show: true,
            position: 'insideRight'
          },
          data: res.data
        };
      });
      this.myChart = this.$echarts.init(document.getElementById(this.id));
      var option = {
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            // 坐标轴指示器，坐标轴触发有效
            type: 'shadow' // 默认为直线，可选为：'line' | 'shadow'

          }
        },
        legend: {
          data: legend
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        yAxis: {
          type: 'value'
        },
        xAxis: {
          type: 'category',
          data: xAxisData
        },
        series: data
      };
      this.myChart.setOption(option, true);
    }
  }
};
