import { queryDeviceBinds, updateDeliver, queryCurve } from "@/api/project/iot/IOT/db";
export default {
  name: "DBTimeData",
  props: {
    itemData: {
      type: Object,
      default: undefined
    },
    code: {
      type: String,
      default: undefined
    }
  },
  watch: {
    itemData: function itemData(val) {
      if (val) {
        this.getList();
      }
    }
  },
  data: function data() {
    return {
      listLoading: false,
      theight: 0,
      //表格高度
      list: [],
      tableKey: 0,
      //表格索引
      pointCode: undefined,
      listQuery: {
        pointEndTime: undefined,
        pointStartTime: undefined
      },
      dialogFormVisible: false,
      chartList: null
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      this.listLoading = true;
      queryDeviceBinds(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(function (response) {
        _this2.$emit('getSubList', response.data);

        _this2.list = response.data;
        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    // 交付
    pay: function pay(row, pointCode) {
      var _this3 = this;

      this.$confirm('此操作将交付所选中数据, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        updateDeliver(_this3.code, _this3.itemData.gatewayCode, _this3.itemData.deviceCode, pointCode).then(function () {
          _this3.getList();

          _this3.$message({
            title: '成功',
            message: '交付成功',
            type: 'success',
            duration: 2000
          });
        });
      }).catch(function () {
        _this3.$message({
          type: 'info',
          message: '已取消交付'
        });
      });
    },
    // 查看曲线
    handleViewCurves: function handleViewCurves(pointCode) {
      this.pointCode = pointCode;
      this.dialogFormVisible = true;
      this.handleQuery();
    },
    // 搜索曲线
    handleQuery: function handleQuery() {
      var _this4 = this;

      queryCurve(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.pointCode, this.listQuery).then(function (res) {
        _this4.chartList = res.data;

        _this4.drawLine();
      });
    },
    drawLine: function drawLine() {
      var xAxisData = this.chartList.map(function (item) {
        return item.pointTime;
      });
      var data = this.chartList.map(function (item) {
        return {
          value: item.pointValue,
          name: item.pointName
        };
      }); // 基于准备好的dom，初始化echarts实例

      var myChart = this.$echarts.init(this.$refs.myChart); // 绘制图表

      myChart.setOption({
        title: {
          text: ''
        },
        tooltip: {
          formatter: '{b}<br/>{c}'
        },
        xAxis: {
          data: xAxisData
        },
        yAxis: {},
        series: [{
          name: '',
          type: 'line',
          data: data
        }]
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    }
  }
};
