import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: "/iwms/plans/salesOut/instructs",
    method: 'get',
    params: query
  });
}
export function addRecord(data) {
  //新增
  return request({
    url: "/iwms/plans/salesOut/instructs",
    method: 'post',
    data: data
  });
}
export function deleteRecord(instructCode) {
  //删除
  return request({
    url: "/iwms/plans/salesOut/instructs/".concat(instructCode),
    method: 'delete'
  });
}
export function updateRecord(instructCode, data) {
  //编辑接口
  return request({
    url: "/iwms/plans/salesOut/instructs/".concat(instructCode),
    method: 'patch',
    data: data
  });
}
export function queryRecorditems(instructCode) {
  //查询明细
  return request({
    url: "/iwms/plans/salesOut/instructs/".concat(instructCode),
    method: 'get'
  });
}
export function queryRecorditems1(instructCode) {
  //查询明细
  return request({
    url: "/iwms/plans/salesOut/instructs/".concat(instructCode, "/details"),
    method: 'get'
  });
}
