import Pagination from '@/components/Pagination';
export default {
  name: 'mixTable',
  components: {
    Pagination: Pagination
  },
  props: ['tableConfig', 'oprateWidth', 'dataList', 'oprateBtnConfig', 'listQuery', 'hideSelect', 'inlineOprateConfig', 'theight', 'total'],
  data: function data() {
    return {
      // isSingle: true,
      curChange: false,
      //if the current row change
      clickChange: false,
      //if click another row
      currentClickRow: null,
      currentSelectedRow: null,
      // current select row
      selectedRows: [] // all the selected rows

    };
  },
  methods: {
    handleQuery: function handleQuery() {
      this.$emit('handleQuery', this.listQuery);
    },
    oprateClick: function oprateClick(id) {
      this.$emit('oprateClick', id, this.selectedRows);
    },
    //check if checked return true else return false
    checkSelectStatus: function checkSelectStatus(rows, row) {
      var checkStatus = false;

      if (rows) {
        if (this.selectedRows.length <= rows.length && rows.length != 0) {
          checkStatus = true;
        }
      }

      if (row) {
        if (JSON.stringify(this.selectedRows).indexOf(JSON.stringify(row)) == -1) {
          checkStatus = true;
        }
      }

      return checkStatus;
    },
    //trigger by any change of select or row-click event
    //rows all current select rows
    selectChange: function selectChange(rows) {
      this.selectedRows = rows;

      if (this.clickChange) {
        this.$refs.tb.setCurrentRow(this.currentClickRow);
        this.$emit('selectChange', this.selectedRows, this.currentClickRow);
      }
    },
    //trigger by the table selection checkbox
    //rows：all current select rows  row：current oprate row
    tableSelect: function tableSelect(rows, row) {
      var selectStatus = this.checkSelectStatus(rows);

      if (JSON.stringify(this.currentClickRow) === JSON.stringify(row)) {
        this.clickChange = false;
      } else {
        this.clickChange = true;
        this.currentClickRow = row;
      }

      if (selectStatus) {
        this.currentSelectedRow = row;
      } else {
        this.curChange = false;
      }
    },
    //trigger by click the table row (exclude click the the table selection checkbox)
    //row：current click row
    rowClick: function rowClick(row) {
      if (JSON.stringify(this.currentClickRow) === JSON.stringify(row)) {
        this.clickChange = false;
      } else {
        this.clickChange = true;
      }

      this.currentClickRow = row;
      var selectStatus = this.checkSelectStatus(null, row);

      if (selectStatus) {
        this.currentSelectedRow = row;
        this.$refs.tb.toggleRowSelection(row, true);
      } else {
        if (this.clickChange) {
          this.$refs.tb.setCurrentRow(this.currentClickRow);
          this.$emit('selectChange', this.selectedRows, this.currentClickRow);
        }
      }
    },
    setFirstLine: function setFirstLine() {
      this.clickChange = true;
      this.currentSelectedRow = this.dataList[0];
      this.currentClickRow = this.dataList[0];
      this.$refs.tb.toggleRowSelection(this.dataList[0], true);
    },
    //表格显示条数变化
    handleSizeChange: function handleSizeChange(val) {
      this.$emit('handleSizeChange', val);
    },
    //表格当前页变化
    handleCurrentChange: function handleCurrentChange(val) {
      this.$emit('handleCurrentChange', val);
    },
    handleInlineOprate: function handleInlineOprate(id, index, row) {
      this.$emit('handleInlineOprate', id, index, row);
    }
  }
};
