import * as api from '@/api/project/eas/ESC/pointCheckRecord'; // 增、删、改api引入

import deviceType from "../../components/deviceTypeSelect";
import Pagination from '@/components/Pagination';
export default {
  name: 'singleDeviceSelect',
  components: {
    deviceType: deviceType,
    Pagination: Pagination
  },
  data: function data() {
    return {
      theight: 0,
      //表格高度
      listQuery2: {
        deviceCode: undefined,
        deviceName: undefined,
        modelNumber: undefined,
        typeCode: undefined,
        ifHaveUseDetp: 'Y',
        //从表查询
        currentPage: 1,
        pageSize: 10
      },
      total2: 0,
      tableKey: [0],
      device: [],
      selectDevice: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 185;
    },
    handleQuery2: function handleQuery2() {
      this.listQuery2.currentPage = 1;
      this.queryAddtionData();
    },
    getList: function getList() {
      var _this2 = this;

      api.queryDevice(this.listQuery2).then(function (_ref) {
        var data = _ref.data;
        _this2.device = data.list;
        _this2.total2 = data.pages.count;
      });
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectDevice = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.device.clearSelection();
        this.$refs.device.toggleRowSelection(val.pop());
      }

      this.$refs.device.setCurrentRow(this.selectDevice);
      this.$emit('change', this.selectDevice);
    },
    //主表单行点击方法
    selectRow_line: function selectRow_line(val) {
      this.$refs.device.clearSelection();
      this.$refs.device.toggleRowSelection(val);
    }
  }
};
