import request from '@/utils/request'; //查询手工拉料单列表

export function queryRecords(query) {
  return request({
    url: '/iwms/plans/pullOrders',
    method: 'get',
    params: query
  });
} //查询单个手工拉料单

export function queryRowRecords(pullOrderCode, params) {
  return request({
    url: "/iwms/plans/pullOrders/".concat(pullOrderCode, "/details"),
    method: 'get',
    params: params
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
} //查询收货凭证列表

export function getProofList(query) {
  return request({
    url: "/iwms/stores/receives",
    method: 'get',
    params: query
  });
} //查询单条收货凭证列表

export function getPrintStorageList(query) {
  return request({
    url: "/iwms/stores/receives/".concat(query.receiveOrderNumber, "/items"),
    method: 'get',
    params: query
  });
} //创建拉料入库凭证

export function createProof(data) {
  return request({
    url: "/iwms/stores/receives",
    method: 'post',
    data: data
  });
}
export function recommend(query) {
  //库区库位推荐
  return request({
    url: "/iwms/ruleExecution/locationPropose",
    method: 'POST',
    data: query
  });
}
