import request from '@/utils/request';
export function queryRecords(query) {
  //查询
  return request({
    url: "/easDevice/upkeep/plan",
    method: 'get',
    params: query
  });
}
export function addRecords(data) {
  //新增
  return request({
    url: "/easDevice/upkeep/plan",
    method: 'post',
    data: data
  });
}
export function detailsRecords(maintainPlanCode) {
  //查询单个保养计划
  return request({
    url: "/easDevice/upkeep/plan/".concat(maintainPlanCode),
    method: 'get'
  });
}
export function updateRecords(maintainPlanCode, data) {
  //编辑
  return request({
    url: "/easDevice/upkeep/plan/".concat(maintainPlanCode),
    method: 'patch',
    data: data
  });
}
export function deleteRecords(maintainPlanCode) {
  //删除
  return request({
    url: "/easDevice/upkeep/plan/".concat(maintainPlanCode),
    method: 'delete'
  });
}
export function dispatchRecords(maintainPlanCode) {
  //派工
  return request({
    url: "/easDevice/upkeep/plan/".concat(maintainPlanCode, "/state"),
    method: 'patch'
  });
}
export function timeRecords(data) {
  //日期调整
  return request({
    url: "/easDevice/upkeep/plan/time",
    method: 'patch',
    data: data
  });
} //查询班组

export function queryTeam(teamCode, query) {
  return request({
    url: "/easDevice/team/".concat(teamCode, "/employee"),
    method: 'get',
    params: query
  });
}
