import request from '@/utils/request'; //新品价格确认单查询

export function queryRecords(query) {
  return request({
    url: "/business/price-confirmation",
    method: 'get',
    params: query
  });
} //新品价格确认单明细查询

export function queryRecordsItems(confirmationNo) {
  return request({
    url: "/business/price-confirmation/".concat(confirmationNo, "/item"),
    method: 'get'
  });
} // 新品价格确认单新增

export function createRecords(data) {
  return request({
    url: "/business/price-confirmation",
    method: 'post',
    data: data
  });
} // 新品价格确认单编辑

