import request from '@/utils/request'; //技术评审查询

export function queryRecords(query) {
  return request({
    url: "/business/rfq-reply",
    method: 'get',
    params: query
  });
} //技术评审明细查询

export function queryRecordItems(query) {
  return request({
    url: "/business/rfq-reply/".concat(query.rfqNo, "/technical-proposal"),
    method: 'get',
    params: query
  });
} //技术评审服务器时间查询

export function serverTime(query) {
  return request({
    url: "/business/rfq-reply/system-time",
    method: 'get',
    params: query
  });
} //常规付款方式维护（新增）

export function addRecord(data) {
  return request({
    url: "/business/regularPayment",
    method: 'post',
    data: data
  });
} //技术评审提交（评审）

export function submit(data) {
  return request({
    url: "/business/rfq-reply/handle",
    method: 'patch',
    data: data
  });
} //技术评审（评审）

export function updateRecord(data) {
  return request({
    url: "/business/rfq-reply/technical-evaluation",
    method: 'patch',
    data: data
  });
} //常规付款方式删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/regularPayment/".concat(rowId),
    method: 'delete'
  });
} //常规付款方式维护（生效与失效）

export function updateEffect(rowId, data) {
  return request({
    url: "/business/regularPayment/".concat(rowId, "/state"),
    method: 'patch',
    data: data
  });
}
