//入平台通知api
import request from '@/utils/request'; //查询准入评价列表

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/notices",
    method: 'get',
    params: query
  });
} // 创建入平台通知

export function createRecords(data) {
  return request({
    url: "/SRMsupplier/notices",
    method: 'post',
    data: data
  });
} //更新、发布入平台通知

export function updateRecords(noticeNo, data) {
  return request({
    url: "/SRMsupplier/notices/".concat(noticeNo),
    method: 'patch',
    data: data
  });
} // 删除入平台通知

export function deleteRecords(noticeNo) {
  return request({
    url: "/SRMsupplier/notices/".concat(noticeNo),
    method: 'delete'
  });
} //查询单个入平台通知

export function queryRow(noticeNo) {
  return request({
    url: "/SRMsupplier/notices/".concat(noticeNo),
    method: 'get'
  });
} //查询入平台通知明细列表

export function queryItems(noticeNo, params) {
  return request({
    url: "/SRMsupplier/notices/".concat(noticeNo, "/items"),
    method: 'get',
    params: params
  });
} //分配供应商账号编码

export function recommendSc(params) {
  return request({
    url: "/SRMsupplier/notices/supplierCode",
    method: 'get',
    params: params
  });
} //查询已经生成考察计划并已经评分的提案详情

export function getPropsalDetail(params) {
  return request({
    url: "/SRMsupplier/investigates/evaluation-proposal",
    method: 'get',
    params: params
  });
}
