import * as api from '@/api/project/mes/productionExecution/uploadMaterial'; // import { mapState } from 'vuex' // 基本数据引入

import { getWorkstation } from '@/utils/auth';
export default {
  name: 'uploadMaterial',
  // components: { Pagination },
  data: function data() {
    return {
      status: undefined,
      workOrderList: [],
      temp: {
        stationCode: getWorkstation(),
        workOrderCode: undefined,
        workOrderCodeTemp: undefined,
        barCode: undefined,
        materielCode: undefined,
        materielName: undefined,
        quantity: undefined,
        supplierName: undefined
