    queryMaterial: function queryMaterial() {
      var _this = this;

      if (!this.temp.workOrderCode) {
        this.$message({
          title: '警告',
          message: '请选择随工单号',
          type: 'warning'
        });
        return;
      }

      if (!this.temp.stationCode) {
        this.$message({
          title: '警告',
          message: '请在登录时选择工位',
          type: 'warning'
        });
        return;
      }

      if (!this.temp.barCode) {
        this.$message({
          title: '警告',
          message: '请输入条码',
          type: 'warning'
        });
        return;
      }

      api.queryRecords(this.temp.barCode, {
        barcodeSearchEnum: 'FIVE'
      }).then(function (res) {
        _this.status = res.data.materielCode;

        if (res.data.materielCode) {
          _this.temp.materielCode = res.data.materielCode;
          _this.temp.materielName = res.data.materielName;
          _this.temp.supplierName = res.data.supplierName;
        } else {
          _this.$message({
            title: '警告',
            message: '搜不到物料请重新输入条码查询',
            type: 'warning'
          });
        }
      });
    },
    upload: function upload() {
      var _this2 = this;

      if (this.temp.barCode) {
        if (this.status !== undefined) {
          this.temp.materialNo = this.temp.materielCode;
          this.temp.materialBarCode = this.temp.barCode;

          if (this.temp.quantity) {
            api.loadMaterial(this.temp).then(function (res) {
              if (res.status == 201) {
                _this2.$message({
                  title: '成功',
                  message: '上料成功',
                  type: 'success'
                });

                _this2.resetTemp();
              }
            });
          } else {
            this.$message({
              title: '警告',
              message: '数量不能为空',
              type: 'warning'
            });
          }
        } else {
          this.$message({
            title: '警告',
            message: '搜不到物料请重新输入条码查询',
            type: 'warning'
          });
        }
      } else {
        this.$message({
          title: '警告',
          message: '请输入条码',
          type: 'warning'
        });
      }
    },
    getList: function getList() {
      var _this3 = this;

      //查询随工单
      api.queryWorkOrder(this.listQuery).then(function (res) {
        _this3.workOrderList = res.data;
      });
    },
    resetTemp: function resetTemp() {
      this.temp = {
        stationCode: getWorkstation(),
        workOrderCode: undefined,
        workOrderCodeTemp: undefined,
        barCode: undefined,
        materielCode: undefined,
        materielName: undefined,
        quantity: undefined,
        supplierName: undefined
      };
    },
    confirm: function confirm() {
      this.temp.workOrderCodeTemp = this.temp.workOrderCode;
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    }
  }
};
