import request from '@/utils/request'; //RFQ配置查询

export function queryRecords(query) {
  return request({
    url: "/basic/rqfConfig",
    method: 'get',
    params: query
  });
} //RFQ配置（新增）

export function addRecord(data) {
  return request({
    url: "/basic/rqfConfig",
    method: 'post',
    data: data
  });
} //RFQ配置（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/rqfConfig/".concat(rowId),
    method: 'patch',
    data: data
  });
} //RFQ配置删除

export function deleteRecord(rowId) {
  return request({
    url: "/basic/rqfConfig/".concat(rowId),
    method: 'delete'
  });
}
