import request from '@/utils/request'; //主表班组信息（查询）

export function queryRecords(query) {
  return request({
    url: "/easDevice/team",
    method: 'get',
    params: query
  });
} //主表班组信息（新增）

export function addRecord(data) {
  return request({
    url: "/easDevice/team",
    method: 'post',
    data: data
  });
} //主表班组信息（编辑）

