export function selectlists(query, filterEmployeeCode) {
  var code = '';

  if (filterEmployeeCode) {
    filterEmployeeCode.forEach(function (i, index) {
      if (index == 0) {
        code += "filterEmployeeCodes=".concat(i);
      } else {
        code += "&filterEmployeeCodes=".concat(i);
      }
    });
  }

  var url = code ? "/keyguard/employees?".concat(code) : "/keyguard/employees";
  return request({
    url: url,
    method: 'get',
    params: query
  });
}
