  getDicData: function getDicData(context, dicCodes) {
    var _loop = function _loop(i) {
      if (state[dicCodes[i]].length == 0) {
        getRecord(dicCodes[i]).then(function (res) {
          res = res.data.dictItemDTOs;
          context.commit('getDicData', {
            dicCodes: dicCodes[i],
            res: res
          });
        });
      }
    };

