  name: "wzskncontentRegister",
  components: {
    changeModuleSelect: changeModuleSelect,
    Pagination: Pagination
  },
  data: function data() {
    return {
      theight: 0,
      list: null,
      isSingle: true,
      //表格是否单选 点击各按钮根据流程逻辑控制单多选
      currentSelectedRow: null,
      //当前选中的行
      selectedRows: null,
      //多选时选中的所有行
      listQuery: {
        //查询
        page: true,
        currentPage: 1,
        pageSize: 15
      },
      total: 0,
      listTemp: null,
      mainFormList: {
        orgCode: '',
        accountDate: this.timeFormat()
      },
      configBusiness: {
        db: '0908',
        cz: '0907',
        zz: '0906'
      },
      isEdit: false,
      isSaveEdit: false,
      selectRows: [],
      rules: {
        orgCode: [{
          required: true,
          message: ' ',
          trigger: 'blur'
        }],
        warehouseCode: [{
          required: true,
          message: ' ',
          trigger: 'blur'
        }],
        wareAreaCode: [{
          required: true,
          message: ' ',
          trigger: 'blur'
        }],
        wareLocationCode: [{
          required: true,
